import { useEffect, useState } from 'react'
import styles from "./Events.module.css"

function Events() {
    const [events, setEvents] = useState([]);
    useEffect(() => {
        const getEvents = async () => {
            var eventsAPI = 'http://localhost:5000/shopevent/get_all';
            const res = await (await fetch(eventsAPI)).json();
            setEvents(res);
        };
        getEvents();
    }, []);
    function handleDeleteEvent(event) {
        var eventsDeleteAPI = 'http://localhost:5000/shopevent/delete';
        fetch(eventsDeleteAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(event)
        })
            .then(res => res.json())
            .then(() => {
                console.log(event);
                var e = document.getElementById(event.id);
                if (e) {
                    e.remove();
                }
            })
    }

    // console.log(events);

    return (
        // <div className={styles.events}>
        //     <h1 className={styles.side_bar}>Danh sách sự kiện</h1>
        //     <div className={styles.events_list}>
        //         {events.map(event => (
        //             <ul id={event.id} className={styles.events_list_item}>
        //                 <div className={styles.event_content}>
        //                     <h1 className={styles.name_customer}>{event.name}</h1>
        //                     <span className={styles.time}>SĐT: {event.phone}</span>
        //                     <br/>
        //                     <span className={styles.time}>Số người tham gia: {event.number} người</span>
        //                     <br/>
        //                     <span className={styles.time}>Thời gian: {event.time}</span>
        //                 </div>
        //                 <button className={styles.btn_delete} onClick={() => handleDeleteEvent(event)}>delete</button>
        //             </ul>

        //         ))}
        //     </div>
        // </div>
        
    <TableContainer >
    <Table sx={{ minWidth: 650 }} size="small" aria-label="a dense table">
      <TableHead>
        <TableRow>
          <TableCell>Tên khách hàng</TableCell>
          <TableCell align="right">Số diện thoại</TableCell>
          <TableCell align="right">Số người tham gia</TableCell>
          <TableCell align="right">Thời gian</TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        {rows.map((row) => (
          <TableRow
            key={row.name}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
          >
            <TableCell component="th" scope="row">
              {row.name}
            </TableCell>
            <TableCell align="right">{row.calories}</TableCell>
            <TableCell align="right">{row.fat}</TableCell>
            <TableCell align="right">{row.carbs}</TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>
  </TableContainer>
    );

}

export default Events
