class userStore {
    useDetail = {};
    isSignined = 'false'; isSignuped='false'; isSignuoted = '';

    setUserDetail() {
        this.userDetail = JSON.parse(localStorage.getItem('userDetail'));
        this.isSignined = localStorage.getItem('isSignined');
        this.isSignuped = localStorage.getItem('isSignuped');
        this.isSignuoted = localStorage.getItem('isSignouted');   
        localStorage.removeItem('isSignined');

    }
    handleSignout() {
        localStorage.removeItem('userDetail');
        localStorage.setItem('isSignined', 'false');
    }
}

export default new userStore();
