import { useEffect, useState } from 'react'
import styles from "./Events.module.css"
function Events() {
    const [events, setEvents] = useState([]);
    useEffect(() => {
        const getEvents = async () => {
            var eventsAPI = 'http://localhost:5000/shopevent/get_all';
            const res = await (await fetch(eventsAPI)).json();
            setEvents(res);
        };
        getEvents();
    }, []);
    // function handleDeleteEvent(event) {
    //     var eventsDeleteAPI = 'http://localhost:5000/shopevent/delete';
    //     fetch(eventsDeleteAPI, {
    //         method: 'DELETE',
    //         headers: {
    //             'Content-Type': 'application/json'
    //         },
    //     })
    //         .then(res => res.json())
    //         .then(res => setEvents(res))
    // }
    function handleDeleteEvent(event){
        useEffect(() => {
            const getEvents = async () => {
                var eventsAPI = 'http://localhost:5000/shopevent/delete';
                const res = await (await fetch(eventsAPI)).json();
                setEvents(res);
            };
            getEvents();
        }, []);
    }
    console.log(events);

    return (
        <div className={styles.events_list}>
            danh sách sự kiện
            <ul>
                {events.map(event => (
                    <ul className={styles.event}>
                        <div className={styles.event_content}>
                            <h1>{event.name}</h1>
                            <span>{event.time}</span>
                        </div>
                        <button onClick={()=>handleDeleteEvent(event)}>delete</button>
                    </ul>
                    
                ))}
                
            </ul>
        </div>
    )

}

export default Events
