import { useEffect, useState } from 'react'
import styles from "./Events.module.css"
function Events() {
    const [events, setEvents] = useState([]);
    useEffect(() => {
        const getEvents = async () => {
            var eventsAPI = 'http://localhost:5000/shopevent/get_all';
            const res = await (await fetch(eventsAPI)).json();
            setEvents(res);
        };
        getEvents();
    }, []);
    function handleDeleteEvent(event) {
        var eventsDeleteAPI = 'http://localhost:5000/shopevent/delete';
        fetch(eventsDeleteAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(event)
        })
            .then(res => res.json())
            .then(function(){
                var event = document.getElementById(event.id);
                if(event){
                    console.log();
                    event.remove();
                }
            })
    }

    console.log(events);

    return (
        <div className={styles.events_list}>
            danh sách sự kiện
                {events.map(event => (
                    <ul id={event.id} className={styles.event}>
                        <div className={styles.event_content}>
                            <h1>{event.name}</h1>
                            <span>{event.time}</span>
                        </div>
                        <button onClick={() => handleDeleteEvent(event)}>delete</button>
                    </ul>

                ))}
        </div>
    )

}

export default Events
