import { useEffect, useState } from 'react'
import styles from "./Events.module.css"

function Events() {
    const [events, setEvents] = useState([]);
    useEffect(() => {
        const getEvents = async () => {
            var eventsAPI = 'http://localhost:5000/shopevent/get_all';
            const res = await (await fetch(eventsAPI)).json();
            setEvents(res);
        };
        getEvents();
    }, []);
    function handleDeleteEvent(event) {
        var eventsDeleteAPI = 'http://localhost:5000/shopevent/delete';
        fetch(eventsDeleteAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(event)
        })
            .then(res => res.json())
            .then(() => {
                console.log(event);
                var e = document.getElementById(event.id);
                if (e) {
                    e.remove();
                }
            })
    }

    // console.log(events);

    return (
        <div className={styles.events}>
            <h1 className={styles.side_bar}>Danh sách sự kiện</h1>
            <div className={styles.events_list}>
                {events.map(event => (
                    <ul id={event.id} className={styles.events_list_item}>
                        <div className={styles.event_content}>
                            <h1 className={styles.name_customer}>{event.name}</h1>
                            
                            <span className={styles.time}>Thời gian: {event.time}</span>
                        </div>
                        <button className={styles.btn_delete} onClick={() => handleDeleteEvent(event)}>delete</button>
                    </ul>

                ))}
            </div>
        </div>
    );

}

export default Events
