import { useEffect, useState } from 'react'

import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@material-ui/core';
import styles from "./Products.module.css"

function Products() {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        const getProduct = async () => {
            const url = "http://localhost:5000/product/get_all";
            const res = await (await fetch(url)).json();
            setProducts(res);
        };
        getProduct();
    }, []);

    function handleDeleteProduct(product) {
        var ProductsDeleteAPI = 'http://localhost:5000/product/delete';
        var e = document.getElementById(product.id);
        if (e) {
            e.remove();
        }
        fetch(ProductsDeleteAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(product)
        })
            .then(res => res.json())
    }
    function handleAddProduct(product) {
        var ProductsAddAPI = 'http://localhost:5000/product/add';
        fetch(ProductsAddAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(product)
        })
            .then(res => res.json())
            .then((res) => setProducts(res))
    }

    // console.log(Products);

    return (
        <div className={styles.products}>
            <div className={styles.header}>
            <h1 className={styles.label}>Danh sách sản phẩm</h1>
            <Link to='ThemNhanVien' className={styles.AddStaff_link}> Thêm nhân viên</Link>
            </div>
            <TableContainer component={Paper}>
                <Table aria-label="a dense table">
                    <TableHead>
                        <TableRow>
                            <TableCell align="center">Hình ảnh</TableCell>
                            <TableCell align="center">Tên sản phẩm</TableCell>
                            <TableCell align="center">Phân loại</TableCell>
                            <TableCell align="center">Giá tiền</TableCell>
                            <TableCell align="center">Số lượng còn lại</TableCell>
                            <TableCell align="center">Hành động</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {products.map((product) => (
                            <TableRow
                                id={product.id}
                                key={product.id}
                                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                style={{'padding':'4px 8px'}}
                            >
                                <TableCell component="th" scope="row" align="center">
                                    <img src={product.image} className={styles.img}></img>
                                </TableCell>
                                <TableCell align="center">{product.name}</TableCell>
                                <TableCell align="center">{product.price}</TableCell>
                                <TableCell align="center">{product.stock}</TableCell>
                                <TableCell align="center">
                                    <button className={styles.btn_delete} onClick={() => handleDeleteProduct(product)}>Hủy
                                    </button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );

}

export default Products
