import {useEffect, useState} from 'react'

import {Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper} from '@material-ui/core';
import styles from "./Products.module.css"

function Products() {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        const getProduct = async () => {
            const url = "http://localhost:5000/product/get_all";
            const res = await (await fetch(url)).json();
            setProducts(res);
        };
        getProduct();
    }, []);

    function handleDeleteProduct(product) {
        var ProductsDeleteAPI = 'http://localhost:5000/product/delete';
        var e = document.getElementById(product.id);
        if (e) {
            e.remove();
        }
        fetch(ProductsDeleteAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(product)
        })
            .then(res => res.json())
    }
    function handleAddProduct(product) {
        var ProductsAddAPI = 'http://localhost:5000/product/add';
        fetch(ProductsAddAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(product)
        })
            .then(res => res.json())
            .then((res)=>setProducts(res))
    }

    // console.log(Products);

    return (
        <div className={styles.Products}>
            <h1 className={styles.side_bar}>Danh sách sản phẩm</h1>
            <TableContainer component={Paper}>
                <Table aria-label="a dense table">
                    <TableHead>
                        <TableRow>
                            <TableCell className={styles.item}>Sản phẩm</TableCell>
                            <TableCell align="right">Giá tiền</TableCell>
                            <TableCell align="right">Số lượng còn lại</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {products.map((product) => (
                            <TableRow
                                id={product.id}
                                key={product.id}
                                sx={{'&:last-child td, &:last-child th': {border: 0}}}
                            >
                                <TableCell component="th" scope="row">
                                    {product.image}
                                    {product.name}
                                </TableCell>
                                <TableCell align="right">{product.price}</TableCell>
                                <TableCell align="right">{product.stock}</TableCell>
                                <TableCell align="right">
                                    <button className={styles.btn_delete} onClick={() => handleDeleteProduct(product)}>Hủy
                                    </button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );

}

export default Products
