import { useEffect, useState } from 'react'
import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import styles from "./Events.module.css"

function Events() {
    const [events, setEvents] = useState([]);
    useEffect(() => {
        const getEvents = async () => {
            var eventsAPI = 'http://localhost:5000/shopevent/get_all';
            const res = await (await fetch(eventsAPI)).json();
            setEvents(res);
        };
        getEvents();
    }, []);
    function handleDeleteEvent(event) {
        var eventsDeleteAPI = 'http://localhost:5000/shopevent/delete';
        fetch(eventsDeleteAPI, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(event)
        })
            .then(res => res.json())
            .then(() => {
                console.log(event);
                var e = document.getElementById(event.id);
                if (e) {
                    e.remove();
                }
            })
    }

    // console.log(events);

    return (
        // <div className={styles.events}>
        //     <h1 className={styles.side_bar}>Danh sách sự kiện</h1>
        //     <div className={styles.events_list}>
        //         {events.map(event => (
        //             <ul id={event.id} className={styles.events_list_item}>
        //                 <div className={styles.event_content}>
        //                     <h1 className={styles.name_customer}>{event.name}</h1>
        //                     <span className={styles.time}>{event.time}</span>
        //                 </div>
        //                 <button className={styles.btn_delete} onClick={() => handleDeleteEvent(event)}>delete</button>
        //             </ul>

        //         ))}
        //     </div>
        // </div>
        <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Dessert (100g serving)</TableCell>
              <TableCell align="right">Calories</TableCell>
              <TableCell align="right">Fat&nbsp;(g)</TableCell>
              <TableCell align="right">Carbs&nbsp;(g)</TableCell>
              <TableCell align="right">Protein&nbsp;(g)</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {events.map((event) => (
              <TableRow
                key={event.name}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {event.name}
                </TableCell>
                <TableCell align="right">{event.phone}</TableCell>
                <TableCell align="right">{event.time}</TableCell>
                <TableCell align="right">{event.number}</TableCell>
                {/* <TableCell align="right">{event.protein}</TableCell> */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
    

}

export default Events
